import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { StateHandler } from "../../../StateHandler";

const Cart = () => {
  const state = useContext(StateHandler);
  const [cart] = state.userApi.cart;

  if (cart.length === 0) {
    return (
      <div className="container text-center mt-3 text-info">
        <h2>Cart is empty</h2>
      </div>
    );
  }
  return (
    <div>
      <Link className="btn btn-secondary" to="/">
        <i className="fas fa-arrow-circle-left"></i> Back to Products
      </Link>
      {cart.map((product) => (
        <div className="mt-4 ml-3">
          <div className="mt-5 ">
            <div
              className="card container shadow p-3 mb-5 bg-white rounded"
              style={{ width: "25rem" }}
            >
              <img
                className="card-img-top pt-3"
                src={product.images.url}
                alt=""
              />
              <div className="card-body">
                <h3 className="card-title text-uppercase text-info">
                  {product.title}
                </h3>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item">#Id: {product.product_id}</li>
                  <li className="list-group-item">
                    Content: {product.content}
                  </li>
                  <li className="list-group-item">
                    Price: ${product.price * product.quantity}
                  </li>
                  <li className="list-group-item btn btn-danger">x</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Cart;
