import React, { useContext, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { StateHandler } from "../../../StateHandler";

function Detail() {
  const params = useParams();
  const state = useContext(StateHandler);
  const [products] = state.productAPi.products;
  const [detail, setDetail] = useState([]);

  useEffect(() => {
    if (params) {
      products.forEach((product) => {
        if (product._id === params.id) {
          setDetail(product);
        }
      });
    }
  }, [params, products]);

  if (detail.length === 0) {
    return null;
  }

  return (
    <div className="mt-5 ">
      <div className="card container shadow p-3 mb-5 bg-white rounded" style={{ width: "25rem" }}>
        <img className="card-img-top pt-3" src={detail.images.url} alt="" />
        <div className="card-body">
          <h3 className="card-title">{detail.title}</h3>
          <h5 className="card-text">Description: {detail.description}</h5>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">Id: {detail.product_id}</li>
            <li className="list-group-item">Content: {detail.content}</li>
            <li className="list-group-item">Price: ${detail.price}</li>
            <li className="list-group-item">Sold: {detail.sold}</li>
          </ul>
          <p className="card-text"></p>
          <Link className="btn btn-primary" to="/cart">
            Buy Now
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Detail;
