import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { StateHandler } from "../../../StateHandler";

const Cart = () => {
  const state = useContext(StateHandler);
  const [cart] = state.userApi.cart;

  if (cart.length === 0) {
    return (
      <div className="container text-center mt-3 text-info">
        <h2>Cart is empty</h2>
      </div>
    );
  }
  return (
    <div className="card">
      <div className="mt-4 ml-3">
        <Link className="btn btn-secondary" to="/">
          <i className="fas fa-arrow-circle-left"></i> Back to Products
        </Link>
      </div>

      {cart.map((product) => (
        <div className="mt-4 ml-3">
          <div className="mt-5 ">
            <div
              className="card container shadow p-3 mb-5 bg-white rounded"
              style={{ width: "35rem" }}
            >
              <div className="d-flex flex-wrap">
                <div>
                  <img
                    className="card-img-top pt-3"
                    style={{ width: "15rem" }}
                    src={product.images.url}
                    alt=""
                  />
                </div>
                <div className="card-body">
                  <h3 className="card-title text-uppercase text-info">
                    {product.title}
                  </h3>
                  <ul className="list-group list-group-flush">
                    <li className="list-group-item">
                      #Id: {product.product_id}
                    </li>
                    <li className="list-group-item">
                      Content: {product.content}
                    </li>
                    <li className="list-group-item">
                      Price: ${product.price * product.quantity}
                    </li>
                    <div className="d-flex">
                      <button className="btn btn-light">-</button>
                      <span>{product.quantity}</span>
                      <button>+</button>
                    </div>
                    <li className="list-group-item">
                      <div className="btn btn-danger">
                        <i className="fas fa-trash-alt"></i>
                      </div>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      ))}
      <div className="ml-3">
        <h4>Total: $0</h4>
      </div>
    </div>
  );
};

export default Cart;
