import React, { useContext, useState } from "react";
import { StateHandler } from "../../StateHandler";

const Category = () => {
  const state = useContext(StateHandler);
  const [categories] = state.categoriesAPi.categories;
  const [category, setCategory] = useState("");

  return (
    <div className="container">
      <div className="card p-4 m-5 shadow p-3 mb-5 bg-white rounded">
        <form>
          <label htmlFor="formGroupExampleInput2">Category</label>
          <input
            type="text"
            class="form-control"
            id="formGroupExampleInput2"
            placeholder="Add Category"
            required
          />
          <button type="submit" className="btn btn-dark mt-3">
            <i className="fas fa-save"></i>
          </button>
        </form>
        <div className="card mt-3 pb-3 shadow-sm p-3 mb-5 bg-white rounded">
          {categories.map((category) => (
            <div key={category._id}>
              <p className="mt-3 ml-3">{category.name}</p>
              <div className="ml-3">
                <button className="btn btn-dark mr-3">
                  <i className="far fa-edit"></i>
                </button>
                <button className="btn btn-danger">
                  <i className="far fa-trash-alt"></i>
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Category;
