import React, { useContext } from "react";
import { StateHandler } from "../../../StateHandler";

const Cart = () => {
  const state = useContext(StateHandler);
  const [cart] = state.userApi.cart;

  if (cart.length === 0) {
    return (
      <div className="container text-center">
        <h2>Cart is empty</h2>
      </div>
    );
  }
  return <div>Cart</div>;
};

export default Cart;
