import React, { useContext, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { StateHandler } from "../../../StateHandler";

function Detail() {
  const params = useParams();
  const state = useContext(StateHandler);
  const [products] = state.productAPi.products;
  const [detail, setDetail] = useState([]);

  useEffect(() => {
    if (params) {
      products.forEach((product) => {
        if (product._id === params.id) {
          setDetail(product);
        }
      });
    }
  }, [params, products]);

  if (detail.length === 0) {
    return null;
  }

  return (
    <div class="card" style={{ width: "18rem" }}>
      <img class="card-img-top" src={detail.images.url} alt="" />
      <div class="card-body">
        <h3 class="card-title">{detail.title}</h3>
        <p class="card-text">Description: {detail.description}</p>
        <ul class="list-group list-group-flush">
          <li class="list-group-item">Id: {detail.product_id}</li>
          <li class="list-group-item">Content: {detail.content}</li>
          <li class="list-group-item">Price: ${detail.price}</li>
          <li class="list-group-item">Sold: {detail.sold}</li>
        </ul>
        <p class="card-text"></p>
        <Link class="btn btn-primary" to="/cart">
          Buy Now
        </Link>
      </div>
    </div>
  );
}

export default Detail;
