import React, { Fragment, useContext, useState } from "react";
import { Link } from "react-router-dom";
import { StateHandler } from "../../../StateHandler";
import axios from "axios";

function ProductItem({ product, isAdmin }) {
  const state = useContext(StateHandler);
  const addCart = state.userApi.addCart;
  const [loading, setLoading] = useState(false);
  const [token] = state.token;

  const deleteProduct = async (id, public_id) => {
    try {
      setLoading(true);
      const destroyImg = axios.post(
        "/api/destroy",
        { public_id },
        {
          headers: { Authorization: token },
        }
      );
      const deleteProduct = axios.delete(`/api/products/${id}`, {
        headers: { Authorization: token },
      });

      await destroyImg;
      await deleteProduct;
      alert("Product deleted..");

      setLoading(false);
    } catch (err) {
      alert(err.response.data.msg);
    }
  };
  return (
    <div
      className="card m-3 shadow p-3 mb-5 bg-white rounded"
      style={{ width: "25rem", height: "40rem" }}
    >
      {isAdmin && <input type="checkbox" checked={product.checked} />}
      <img className="card-img-top" src={product.images.url} alt="" />
      <div className="card-body">
        <h5 className="card-title">{product.title}</h5>
        <p className="card-text">Description: {product.description}</p>
      </div>
      <ul className="list-group list-group-flush">
        <li className="list-group-item text-primary">
          Price: ${product.price}
        </li>
      </ul>
      <div className="card-body">
        {isAdmin ? (
          <div className="d-flex justify-content-between">
            <div>
              <Link className="btn btn-dark btn-lg" to="#">
                Edit
              </Link>
            </div>
            <div>
              <Link
                className="btn btn-danger btn-lg"
                deleteProduct={deleteProduct}
              >
                Delete
              </Link>
            </div>
          </div>
        ) : (
          <div className="d-flex justify-content-between">
            <div>
              <Link
                className="btn btn-dark btn-lg"
                to="/"
                onClick={() => addCart(product)}
              >
                Buy
              </Link>
            </div>
            <div>
              <Link
                className="btn btn-info btn-lg"
                to={`detail/${product._id}`}
              >
                View
              </Link>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default ProductItem;
