import React, { useContext, useState } from "react";
import { StateHandler } from "../../../StateHandler";
import axios from "axios";
import { useParams } from "react-router-dom";

const initState = {
  product_id: "",
  title: "",
  price: 0,
  description: "Product",
  content: "Content",
  category: "",
  _id: "",
};

function AddProduct() {
  const state = useContext(StateHandler);
  const [product, setProduct] = useState(initState);
  const [categories] = state.categoriesAPi.categories;
  const [images, setImages] = useState(false);
  const [isAdmin] = state.userApi.isAdmin;

  const handleFileUpload = async (e) => {
    e.preventDefault();
    try {
      if (!isAdmin) {
        return alert("Image Upload failed. Please contact your admin.");
      }
      const file = e.target.files[0];
      if (!file) {
        return alert("No file selected.");
      }
      if (file.size > 1024 * 1024) {
        return alert("Size too large!");
      }
    } catch (err) {
      alert(err.response.data.msg);
    }
  };

  return (
    <div className="container mt-4 card p-3">
      <h3 className="text-center text-info">Create a product</h3>
      <form>
        <div className="form-group">
          <label for="id">Id</label>
          <input
            type="text"
            className="form-control"
            id="id"
            placeholder="Please enter id"
          />
        </div>
        <div className="form-group">
          <label for="title">Title</label>
          <input
            type="text"
            className="form-control"
            id="title"
            placeholder="Please enter title"
          />
        </div>
        <div className="form-group">
          <label for="price">Price</label>
          <input
            type="text"
            className="form-control"
            id="price"
            placeholder="Please enter price"
          />
        </div>

        <div className="form-group">
          <label for="description">Description</label>
          <textarea
            className="form-control"
            id="description"
            rows="3"
            placeholder="Please enter description"
          />
        </div>

        <div className="form-group">
          <label for="content">Content</label>
          <textarea
            className="form-control"
            id="content"
            rows="3"
            placeholder="Please enter content"
          />
        </div>

        <div className="form-group">
          <label className="" for="inputGroupSelect01">
            Categories
          </label>

          <select className="custom-select" value={product.category}>
            <option value="">Select a Category</option>
            {categories.map((category) => (
              <option value={category._id} key={category._id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="inputGroupFileAddon01">
              Upload Image
            </span>
          </div>
          <div className="custom-file">
            <input
              type="file"
              className="custom-file-input"
              id="inputGroupFile01"
              aria-describedby="inputGroupFileAddon01"
              name="file"
              onChange={handleFileUpload}
            />
            <label className="custom-file-label" for="inputGroupFile01">
              Choose an Image
            </label>
          </div>
        </div>

        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
}

export default AddProduct;
